import React from 'react';
import logo from './logo.svg';
import './css/App.css';
import './css/Agenda.css';
import Mostrar from './Mostrar';
import Enviar from './Enviar';

class Agenda extends React.Component{
  constructor () {
    super();
    this.state = {
      Nombre: '',
      Apellido: '',
      Telefono: ''
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  
handleSubmit(e) {
    e.preventDefault();
    fetch("http://www.raydelto.org/agenda.php")
    .then(
    function(valor)
    {
        return valor.json();
    }
    ).then(function(valor){
        
        var imprime = document.getElementById("prueba");
        var valores = '';
       
        for(var i = 0; i < valor.length; i++){
       
            valores +='<br><a>Nombre:</a><br>' + valor[i].nombre + 
            "<br><br>Apellido: <br>" + valor[i].apellido + 
            "<br><br>Telefono: <br>" + valor[i].telefono + "<br>"+"<hr>";
          }
          
          imprime.innerHTML = valores;
    });
  }
  render(){
    return (
      <div className="App">
        <header >
              
        </header>
  
        <body className="App-header">
          
        <img src={logo} className="App-logo" alt="logo" />
        <div className="str">Agenda de contacto</div>
      <div>
          <form onSubmit={this.handleSubmit} method="POST" id="enviar" >
                  <label  >Nombre</label>
                  <br></br>
                  <input id="nombre"type="text" placeholder="Nombre" required/>
                  <br></br>
                  <br></br>
                  <label   >Apellido</label>
                  <br></br>
                  <input id="apellido" type="text" placeholder="Apellido" required/>
                  <br></br>
                  <br></br>
                  <label  >Telefono</label>
                  <br></br>
                  <input  id="telefono" type="text" placeholder="Telefono" required />
                  <br></br>
                  <Enviar/>
                  
          </form>
          
           
          <h2>Listado de Contactos</h2>
          <div id="prueba">
              
          <button className="button" type="submit" onclick="datos()" >Mostrar</button>
              
          </div> 
          
      </div>
        </body>
      </div>
     
    );
  }
}

export default Agenda;
