import React, { Component } from 'react';

class Enviar extends Component{

    constructor () {
        super();
        this.state = {
          Nombre: '',
          Apellido: '',
          Telefono: ''
        };
        this.Datos = this.Datos.bind(this);
      }
      
    Datos(e) {
        e.preventDefault();
        fetch("http://www.raydelto.org/agenda.php")
        .then(
        function(valor)
        {
            return valor.json();
        }
        ).then(function(valor){
            
            var imprime = document.getElementById("prueba");
            var valores = '';
           
            for(var i = 0; i < valor.length; i++){
           
                valores +='<br><a>Nombre:</a><br>' + valor[i].nombre + 
                "<br><br>Apellido: <br>" + valor[i].apellido + 
                "<br><br>Telefono: <br>" + valor[i].telefono + "<br>"+"<hr>";
              }
              
              imprime.innerHTML = valores;
        });
      }      
    render(){
        return(
        <div className="Mostrar">
            <button  className="button" type="submit" onClick ={this.handleSubmit}  >Enviar</button>
        </div>

        );
    }

}

export default Enviar;
